tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'загрузка изображения',file:url+'/dialog-v4.htm',width:450,height:235,buttons:[{text:'загрузить',classes:'widget btn primary first abs-layout-item',disabled:true,onclick:'close'},{text:'Close',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Upload an image',icon:'image',text:'загрузка',onclick:jbBox});editor.addMenuItem('jbimages',{text:'загрузка изображения',icon:'image',context:'insert',onclick:jbBox})});